import React from 'react';
import {
    ToggleButtonGroup,
    ToggleButton,
    Box,
    Paper,
    Typography,
} from '@mui/material';
import {
    Build as PartsIcon,
    Kitchen as ApplianceIcon,
} from '@mui/icons-material';
import { styled } from '@mui/material/styles';

const StyledToggleButtonGroup = styled(ToggleButtonGroup)(({ theme }) => ({
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    borderRadius: theme.spacing(2),
    padding: theme.spacing(0.5),
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
    backdropFilter: 'blur(10px)',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    '& .MuiToggleButton-root': {
        border: 'none',
        borderRadius: theme.spacing(1.5),
        padding: theme.spacing(1.5, 3),
        margin: theme.spacing(0.25),
        fontWeight: 500,
        fontSize: '1rem',
        textTransform: 'none',
        transition: 'all 0.3s ease-in-out',
        minWidth: 140,
        '&.Mui-selected': {
            backgroundColor: theme.palette.primary.main,
            color: 'white',
            boxShadow: '0 4px 20px rgba(25, 118, 210, 0.3)',
            transform: 'translateY(-1px)',
            '&:hover': {
                backgroundColor: theme.palette.primary.dark,
            },
        },
        '&:not(.Mui-selected)': {
            color: theme.palette.text.secondary,
            '&:hover': {
                backgroundColor: 'rgba(25, 118, 210, 0.04)',
                transform: 'translateY(-1px)',
            },
        },
    },
}));

const SearchToggle = ({ searchMode, onSearchModeChange, disabled = false }) => {
    const handleToggleChange = (event, newMode) => {
        if (newMode !== null) {
            onSearchModeChange(newMode);
        }
    };

    return (
        <Box
            sx={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                mb: 4
            }}
        >
            <Typography
                variant="h6"
                color="text.secondary"
                sx={{ mb: 2, fontWeight: 400 }}
            >
                What would you like to search for?
            </Typography>

            <StyledToggleButtonGroup
                value={searchMode}
                exclusive
                onChange={handleToggleChange}
                aria-label="search mode"
                disabled={disabled}
            >
                <ToggleButton
                    value="appliances"
                    aria-label="search appliances"
                    disabled={disabled}
                >
                    <ApplianceIcon sx={{ mr: 1 }} />
                    Search Appliances
                </ToggleButton>

                <ToggleButton
                    value="parts"
                    aria-label="search parts"
                    disabled={true} // Always disabled as requested
                >
                    <PartsIcon sx={{ mr: 1 }} />
                    Search Parts
                </ToggleButton>
            </StyledToggleButtonGroup>

            {searchMode === 'parts' && (
                <Typography
                    variant="body2"
                    color="text.secondary"
                    sx={{ mt: 1, textAlign: 'center', fontStyle: 'italic' }}
                >
                    Parts search coming soon...
                </Typography>
            )}

            {searchMode === 'appliances' && (
                <Typography
                    variant="body2"
                    color="primary.main"
                    sx={{ mt: 1, textAlign: 'center', fontWeight: 500 }}
                >
                    Search our complete appliance inventory
                </Typography>
            )}
        </Box>
    );
};

export default SearchToggle;
